        public override void OnCreate(SQLiteDatabase db)
        {
            string sql = "CREATE TABLE  DeductionRule ( Id  INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE,  RuleCode  VARCHAR NOT NULL,  SubRuleCode  VARCHAR,  RuleName  VARCHAR NOT NULL,  ExamItemId  INTEGER NOT NULL,  DeductedScores  INTEGER NOT NULL,  IsRequired  BOOL NOT NULL,  IsAuto  BOOL NOT NULL,  DeductedReason  VARCHAR,  VoiceText  VARCHAR,  VoiceFile  VARCHAR,  CreatedOn  DATETIME)";
            db.ExecSQL(sql);
            sql = "CREATE TABLE  ExamItem ( Id  INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE,  ItemCode  VARCHAR NOT NULL  UNIQUE,  ItemName  VARCHAR NOT NULL,  VoiceText  VARCHAR,  VoiceFile  VARCHAR,  EndVoiceText  VARCHAR,  EndVoiceFile  VARCHAR,  SequenceNumber  INTEGER  NOT NULL,  MapPointType  INTEGER   NOT NULL,  Remark  VARCHAR,  IconFile  VARCHAR,  ExamItemType  VARCHAR NOT NULL,  CreatedOn  DATETIME,  IsEnable  BOOL)";
            db.ExecSQL(sql);
            sql = "CREATE TABLE  LightExamItem ( Id  INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE,  GroupName  VARCHAR NOT NULL,  LightRules  VARCHAR NOT NULL,  CreatedOn  DateTime NOT NULL,  UpdatedOn  DateTime NOT NULL)";
            db.ExecSQL(sql);
            sql = "CREATE TABLE  LightRule ( Id  INTEGER PRIMARY KEY  NOT NULL,  ItemCode  Varchar NOT NULL,  ItemName  Varchar NOT NULL,  VoiceText  Varchar,  VoiceFile  VARCHAR,  LightRuleType  VARCHAR NOT NULL  DEFAULT(null),  CreatedOn  DateTime NOT NULL,  OperDes  VARCHAR)";
            db.ExecSQL(sql);
            sql = "CREATE TABLE  MapLine ( Id  integer PRIMARY KEY,  CreatedOn  DATETIME NOT NULL,  Name  Varchar NOT NULL  DEFAULT(null),  Remark  TEXT,  Distance  DOUBLE NOT NULL,  CenterLongitude  DOUBLE NOT NULL,  CenterLatitude  DOUBLE NOT NULL,  MinLongitude  DOUBLE NOT NULL,  MinLatitude  DOUBLE NOT NULL,  MaxLongitude  DOUBLE NOT NULL,  MaxLatitude  DOUBLE)";
            db.ExecSQL(sql);
            sql = "CREATE TABLE Setting(Id  integer primary key autoincrement, CreatedOn DATETIME not null, ModifiedOn DATETIME, Key TEXT not null, Value TEXT, GroupName TEXT, Remark TEXT)";
            db.ExecSQL(sql);
            sql = "CREATE TABLE  VehicleModel ( Id  integer PRIMARY KEY,  CreatedOn  DATETIME NOT NULL,  Code  Varchar NOT NULL  DEFAULT(null),  Name  Varchar NOT NULL  DEFAULT(null),  Brand  Varchar DEFAULT(null),  Remark  TEXT)";
            db.ExecSQL(sql);
            sql = "CREATE TABLE  VehicleModelGearSetting ( Id  INTEGER PRIMARY KEY  NOT NULL  UNIQUE,  Gear  INTEGER NOT NULL,  MinRatio  INTEGER NOT NULL,  MaxRatio  INTEGER NOT NULL,  CreatedOn  DateTime NOT NULL,  UpdatedOn  DateTime NOT NULL,  VehicleModelId  INTEGER)";
            db.ExecSQL(sql);

        }


